import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <header className="fixed top-0 left-0 right-0 bg-white shadow z-50">
      <div className="max-w-7xl mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo & Tagline (clickable) */}
        <Link to="/" className="flex flex-col items-start group">
          <div className="flex items-center space-x-2">
            <img
              src="/logo.svg" // ðŸ‘ˆ we'll generate this file
              alt="PlotPulse Logo"
              className="w-8 h-8"
            />
            <span className="text-2xl font-bold text-blue-600 group-hover:text-blue-800 transition">
              PlotPulse
            </span>
          </div>
          <span className="text-sm text-gray-500 italic">
            Feel the heartbeat of every story.
          </span>
        </Link>

        {/* Nav Links */}
        <nav className="hidden md:flex space-x-6">
          <Link to="/" className="hover:text-blue-600 transition">Home</Link>
          <Link to="/add-book" className="hover:text-blue-600 transition">Add Book</Link>
        </nav>

        {/* Auth Buttons */}
        <div className="flex space-x-2">
          {user ? (
            <>
              <button
                className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700"
                onClick={() => navigate('/profile')}
              >
                Profile
              </button>
              <button
                className="px-3 py-1 bg-gray-200 text-gray-700 rounded hover:bg-gray-300"
                onClick={handleLogout}
              >
                Sign Out
              </button>
            </>
          ) : (
            <>
              <Link to="/signup">
                <button className="px-3 py-1 bg-blue-600 text-white rounded hover:bg-blue-700">
                  Signup
                </button>
              </Link>
              <Link to="/login">
                <button className="px-3 py-1 bg-gray-200 text-gray-700 rounded hover:bg-gray-300">
                  Login
                </button>
              </Link>
            </>
          )}
        </div>
      </div>
    </header>
  );
}
