import React, { useContext, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

export default function WelcomePage() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  // If user is already logged in → go to books page
  useEffect(() => {
    if (user) {
      navigate('/books');
    }
  }, [user, navigate]);

  return (
    <div className="welcome-page">
      <div className="welcome-content">
        <div className="welcome-logo">
          <img src="/logo.png" alt="PlotPulse Logo" />
        </div>
        <h1 className="welcome-title">PlotPulse</h1>
        <p className="welcome-tagline">Feel the heartbeat of every story.</p>

        <div className="welcome-buttons">
          <Link to="/login">
            <button className="btn welcome-btn">Login</button>
          </Link>
          <Link to="/signup">
            <button className="btn alt welcome-btn">Sign Up</button>
          </Link>
        </div>
      </div>
    </div>
  );
}
