import React from 'react';
import { BrowserRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import Login from './pages/Login';
import Signup from './pages/Signup';
import BookList from './pages/BookList';
import AddEditBook from './pages/AddEditBook';
import BookDetails from './pages/BookDetails';
import Navbar from './components/Navbar';
import ProtectedRoute from './components/ProtectedRoute';
import WelcomePage from './pages/WelcomePage'; // ✅ new page
import './styles.css';

function AppContent() {
  const location = useLocation();
  const hideNavbarFooterRoutes = ['/']; // ✅ hide navbar/footer on welcome

  return (
    <>
      {!hideNavbarFooterRoutes.includes(location.pathname) && <Navbar />}
      <main className="container">
        <Routes>
          {/* ✅ New welcome route */}
          <Route path="/" element={<WelcomePage />} />

          {/* Existing routes */}
          <Route path="/home" element={<BookList />} />
          <Route path="/signup" element={<Signup />} />
          <Route path="/login" element={<Login />} />
          <Route path="/add-book" element={<ProtectedRoute><AddEditBook /></ProtectedRoute>} />
          <Route path="/edit-book/:id" element={<ProtectedRoute><AddEditBook /></ProtectedRoute>} />
          <Route path="/book/:id" element={<BookDetails />} />
        </Routes>
      </main>
      {!hideNavbarFooterRoutes.includes(location.pathname) && (
        <footer className="footer">Built with ❤️ · BookReview Platform</footer>
      )}
    </>
  );
}

export default function App() {
  return (
    <AuthProvider>
      <Router>
        <AppContent />
      </Router>
    </AuthProvider>
  );
}
