import React from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

export default function WelcomePage() {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-blue-800 to-blue-600 text-white text-center overflow-hidden">
      <motion.h1
        className="text-5xl font-serif mb-4"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        PlotPulse
      </motion.h1>

      <motion.p
        className="text-lg italic mb-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.5, duration: 1 }}
      >
        Feel the heartbeat of every story.
      </motion.p>

      <motion.button
        onClick={() => navigate('/')}
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.95 }}
        className="px-8 py-3 bg-white text-blue-800 font-semibold rounded-full shadow-lg hover:bg-gray-100 transition"
      >
        Enter
      </motion.button>
    </div>
  );
}
