import React, { useContext, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';

export default function WelcomePage() {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();

  // Redirect signed-in users to book list
  useEffect(() => {
    if (user) {
      navigate('/books');
    }
  }, [user, navigate]);

  return (
    <div
      className="welcome-page"
      style={{
        backgroundImage: "url('/image.jpg')", // ðŸ‘‰ Place image in /public folder
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        position: 'relative',
      }}
    >
      {/* Dark Overlay */}
      <div
        style={{
          position: 'absolute',
          inset: 0,
          background: 'rgba(0, 0, 0, 0.55)',
          backdropFilter: 'blur(2px)',
        }}
      ></div>

      <div className="welcome-content" style={{ position: 'relative', zIndex: 2 }}>
        <div className="welcome-logo">
          <img src="/logo.png" alt="PlotPulse Logo" />
        </div>

        <h1 className="welcome-title" style={{ animation: 'fadeInDown 1.2s ease' }}>
          PlotPulse
        </h1>
        <p className="welcome-tagline" style={{ maxWidth: '420px', animation: 'fadeInUp 1.5s ease' }}>
          Discover, review, and share your love for books. PlotPulse connects passionate readers
          from around the world to explore every heartbeat of a story.
        </p>

        <div className="welcome-buttons" style={{ animation: 'fadeIn 2s ease' }}>
          <Link to="/login">
            <button className="btn welcome-btn">Login</button>
          </Link>
          <Link to="/signup">
            <button className="btn alt welcome-btn">Sign Up</button>
          </Link>
        </div>
      </div>
    </div>
  );
}
