import React, { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import { AuthContext } from "../context/AuthContext";

export default function Navbar() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <header className="navbar">
      <div
        className="nav-left"
        onClick={() => navigate("/")}
        style={{ cursor: "pointer" }}
      >
        <div className="logo">
          <img src="/logo.png" alt="PlotPulse Logo" className="logo-img" />
        </div>
        <div>
          <div className="site-title">PlotPulse</div>
          <div className="site-tagline">Feel the heartbeat of every story.</div>
        </div>
      </div>

      {user && (
        <div className="nav-links">
          <Link to="/books">Home</Link>
          <Link to="/add-book">Add Book</Link>
        </div>
      )}

      <div className="nav-actions">
        {user ? (
          <div
            className="profile-menu"
            onMouseEnter={() => setMenuOpen(true)}
            onMouseLeave={() => setMenuOpen(false)}
          >
            <div className="profile-avatar">
              <img src="/user-avatar.png" alt="Profile" />
            </div>

            <AnimatePresence>
              {menuOpen && (
                <motion.div
                  className="dropdown"
                  initial={{ opacity: 0, y: -10 }}
                  animate={{ opacity: 1, y: 0 }}
                  exit={{ opacity: 0, y: -10 }}
                  transition={{ duration: 0.2 }}
                >
                  <Link to="/profile" className="dropdown-item">
                    Edit Profile
                  </Link>
                  <button onClick={handleLogout} className="dropdown-item logout">
                    Logout
                  </button>
                </motion.div>
              )}
            </AnimatePresence>
          </div>
        ) : (
          <>
            <Link to="/signup">
              <button className="btn">Sign Up</button>
            </Link>
            <Link to="/login">
              <button className="btn alt">Login</button>
            </Link>
          </>
        )}
      </div>
    </header>
  );
}
